select

from saas_crm_inventory_record

  status = 1 

    and group_id = #{groupId,jdbcType=INTEGER}


    and company_id = #{companyId,jdbcType=INTEGER}


    and department_id = #{departmentId,jdbcType=INTEGER}


    and admin_id = #{adminId,jdbcType=BIGINT}


    and lan_type = #{lanType,jdbcType=VARCHAR} 

  ORDER BY create_time

select

from saas_crm_inventory_record

  status = 1

    and group_id = #{groupId,jdbcType=INTEGER}


    and company_id = #{companyId,jdbcType=INTEGER}


    and department_id = #{departmentId,jdbcType=INTEGER}


    and admin_id = #{adminId,jdbcType=BIGINT}


    and ( commodity_code LIKE "%"#{searchCond}"%" or code commodity_name "%"#{searchCond}"%")


    and lan_type = #{lanType,jdbcType=VARCHAR} 


ORDER BY create_time desc

  limit #{startIndex},#{pageSize};

select
count(1)
from saas_crm_inventory_record

  status = 1

