mapper.select:
select

from saas_sys_company
 1 = 1

    and group_id = #{groupId,jdbcType=INTEGER} 


    and company_id = #{companyId,jdbcType=INTEGER} 


    and lan_type = #{lanType,jdbcType=VARCHAR} 


ORDER BY create_time desc

  limit #{startIndex},#{pageSize};

